<?php

namespace AppBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * SucursalRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class SucursalRepository extends EntityRepository {

    public function getListSucursalesPorDistancia($arraySucursales, $latitudUM, $longitudUM) {
        //return $arraySucursales;
        foreach ($arraySucursales as $sucursal) {
            /* @var $sucursal Sucursal */
            $sucursal->calcularDistanciaAUsuarioMovil($latitudUM, $longitudUM);
        }

        uasort($arraySucursales, function(Sucursal $a, Sucursal $b) {
            return $a->compareTo($b);
        }
        );

        return $arraySucursales;
    }

    public function getSucursalesLocal($idLocal) {
        $promociones = $this->getEntityManager()
                ->createQuery('SELECT s FROM AppBundle:Sucursal s '
                        . 'LEFT JOIN s.localComercial l '
                        . 'WHERE l.id = :idLocal ')
                ->setParameters(array(
                    'idLocal' => $idLocal
                ))
                ->getResult();
        return $promociones;
    }

    public function getListSucursalLocalPorDistancia($arraySucursales, $latitudUM, $longitudUM) {
        //return $arraySucursales;
        foreach ($arraySucursales as $sucursal) {
            /* @var $sucursal Sucursal */
            $sucursal->calcularDistanciaAUsuarioMovil($latitudUM, $longitudUM);
        }

        uasort($arraySucursales, function(Sucursal $a, Sucursal $b) {
            return $a->compareTo($b);
        }
        );
        $array[] = array($arraySucursales[0]);
        foreach ($arraySucursales as $sucursal) {
            /* @var $sucursal Sucursal */
            $b = true;
            foreach ($array as $s) {
                /* @var $s Sucursal */
                if ($s->getLocalComercial() == $sucursal->getLocalComercial()) {
                    $b = false;
                }
            }
            if ($b) {
                $array->add($sucursal);
            }
        }

        return $array;
    }

}
